import React, { useEffect, useState } from "react";
import "../config/index.css";
import {getRankText, formatAmount, getEmployeeTypeText, getStateText} from "../utils/textUtils";
import createAxiosInstance from "../config/api";

const EmployeeSettings = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEmployee = async () => {
      try {
        const axiosInstance = createAxiosInstance(); // 인스턴스 생성
        const response = await axiosInstance.get("/employees/admin/setting");
        setEmployees(response.data);
        setLoading(false);
      } catch (err) {
        setError("유저 정보를 불러오지 못했습니다.");
        setLoading(false);
        console.error(err); // 에러 로깅
      }
    };
    fetchEmployee();
  }, []);

  const handleAmountChange = (index, value) => {
    const updatedEmployees = [...employees];
    const numericValue = value.replace(/[^0-9]/g, "");
    updatedEmployees[index].monthlyAmount = Number(numericValue);
    setEmployees(updatedEmployees);
  };

  const handleSave = async () => {
    try {
      const axiosInstance = createAxiosInstance(); // 인스턴스 생성
      await axiosInstance.put("/employees/admin/setting", employees);
      alert("적립금이 업데이트 되었습니다.");
    } catch (err) {
      alert("다시 한번 확인해 주세요. " + err.message);
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    // <div className="detail-container">
    //   <h1 className="title">WEAVUS 관리자 페이지</h1>
    //     <table className="table">
    //     <thead>
    //       <tr>
    //         <th className="table-header">직급</th>
    //         <th className="table-header">현재 월 적립 금액</th>
    //       </tr>
    //     </thead>
    //     <tbody>
    //       {employees.map((employee, index) => (
    //         <tr key={index}>
    //           <td className="table-data">{getRankText(employee.rank)}</td>
    //           <td className="table-data">
    //             <input
    //               type="text"
    //               className="input"
    //               value={formatAmount(employee.monthlyAmount)}
    //               onChange={(e) => handleAmountChange(index, e.target.value)}
    //             />
    //           </td>
    //         </tr>
    //       ))}
    //     </tbody>
    //   </table>
    //   <button className="save-button" onClick={handleSave}>
    //     적립금 {""}
    //     업데이트
    //   </button>
    // </div>
      <div className="detail-container">
        <h2>{item.employee.name} 의 퇴직금 상세정보</h2>
        {item ? (
            <div className="detailsTable">
              <div className="tablerow">
                <div className="cell-left">이름</div>
                <div className="cell-right">{item.employee.name}</div>
              </div>
              <div className="tablerow">
                <div className="cell-left">계약 상태</div>
                <div className="cell-right">
                  {getEmployeeTypeText(
                      item.employee.employeeType,
                      item.employee.status
                  )}
                </div>
              </div>
              <div className="tablerow">
                <div className="cell-left">직책</div>
                <div className="cell-right">{getRankText(item.employee.rank)}</div>
              </div>
              <div className="tablerow">
                <div className="cell-left">지급 상태</div>
                <div className="cell-right">{getStateText(item.state)}</div>
              </div>
              <div className="tablerow">
                <div className="cell-left">정직원 전환 일</div>
                <div className="cell-right">{item.employee.conversionDate}</div>
              </div>
              <div className="tablerow">
                <div className="cell-left">예상 퇴직금</div>
                <div className="cell-right">
                  {formatAmount(item.totalAmount ? item.totalAmount : "0")}
                </div>
              </div>
              <div className="tablerow">
                <div className="cell-left">퇴사일</div>
                <div className="cell-right">
                  {item.employee.exitDate ? item.employee.exitDate : "재직중"}
                </div>
              </div>
            </div>
        ) : (
            <p>유저 정보를 찾을 수 없습니다.</p>
        )}
      </div>
  );
};

export default EmployeeSettings;
