import React, { useState } from "react";
import "../../config/index.css";
import createAxiosInstance from "../../config/api";

function PersonnelRegi() {
  const [formData, setFormData] = useState({
    id: "",
    name: "",
    entryDate: "",
    exitDate: "",
    employeeType: "",
    conversionDate: "",
    rank: "",
    status: 0,
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [responseMessage, setResponseMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = async (e) => {
    e.preventDefault();
      const confirmSave = window.confirm("해당 지원자를 저장하시겠습니까?");
      if (!confirmSave) {
        return;
      } else {
      setLoading(true);
      setError("");
      setResponseMessage(""); // 상태 초기화
      try {
        const axiosInstance = createAxiosInstance(); // 인스턴스 생성
        const response = await axiosInstance.post("/personnel/applicant/add", personnelData);
        // 응답 데이터 설정
        setResponseMessage(response.data);
        setFormData({
          id: "",
          name: "",
          entryDate: "",
          exitDate: "",
          employeeType: "",
          conversionDate: "",
          rank: "",
          status: 0,
        });
      } catch (err) {
        if (err.response) {
          setError('등록 실패 : ${err.response.data.message || "서버 오류"}');
        } else if (err.request) {
          setError("등록 실패: 서버로부터 응답이 없습니다." + err.request);
        } else {
          setError("등록 실패 : ${err.message}");
        }
      } finally {
        setLoading(false);
      }
    }
  };


  if (loading) return <p style={styles.errorMessage}>Loading...</p>;
  if (error) return <p style={styles.errorMessage}>{error}</p>;

  return (
    <div className="detail-container">
      <h2 className="title">사원 등록 페이지</h2>
      {responseMessage && !error && (
        <p className="success-message">{responseMessage}</p>
      )}
      {error && !responseMessage && <p className="error-message">{error}</p>}
      <form onSubmit={handleSubmit} className="employee-regi-form">
        <div className="form-group">
          <label>ID</label>
          <input
            type="text"
            name="id"
            value={formData.id}
            onChange={handleChange}
            required
            className="input"
          />
        </div>
        <div className="form-group">
          <label>이름</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            className="input"
          />
        </div>
        <div className="form-group">
          <label>입사일</label>
          <input
            type="date"
            name="entryDate"
            value={formData.entryDate}
            onChange={handleChange}
            required
            className="input"
          />
        </div>
        <div className="form-group">
          <label>퇴사일 (선택)</label>
          <input
            type="date"
            name="exitDate"
            value={formData.exitDate}
            onChange={handleChange}
            className="input"
          />
        </div>
        <div className="form-group">
          <label>직원 유형</label>
          <select
            name="employeeType"
            value={formData.employeeType}
            onChange={handleChange}
            className="input"
            required
          >
            <option value="" disabled>
              직원 유형을 선택해 주세요
            </option>
            <option value="CONTRACT">계약직</option>
            <option value="REGULAR">정규직</option>
          </select>
        </div>
        {formData.employeeType === "REGULAR" && (
          <div className="form-group">
            <label>전환일 (정규직만 입력)</label>
            <input
              type="date"
              name="conversionDate"
              value={formData.conversionDate}
              onChange={handleChange}
              required
              className="input"
            />
          </div>
        )}
        <div className="form-group">
          <label>직급</label>
          <select
            name="rank"
            value={formData.rank}
            onChange={handleChange}
            className="input"
            required
          >
            <option value="" disabled>
              직급을 선택해 주세요
            </option>
            <option value={0}>사원</option>
            <option value={1}>주임</option>
            <option value={2}>계장</option>
            <option value={3}>부장</option>
            <option value={4}>사장</option>
          </select>
        </div>
        <button type="submit" disabled={loading} className="submit-button" onClick={handleSubmit}>
          {loading ? "등록 중..." : "직원 등록"}
        </button>
      </form>
    </div>
  );
};

export default PersonnelRegi;
